mapper.select:
select TIMESTAMPDIFF(second,user_check_begin,user_check_end) as timeDiffer,
`id`,`accept_no`,`custom_id`,`upload_ip`,`upload_time`,`image_path`,`image_name`,`image_size`,`image_special`,`special_msg`,`check_type_id`,`machine_id`,`machine_check_time`,`machine_result`,`machine_check_value`,`machine_result_msg`,`user_check_id`,`user_check_begin`,`user_check_end`,`user_result`,`user_result_msg`,`final_result`,`deal_stage`,`create_time`,`create_user`,`update_time`,`update_user`,`remark` from pc_image_check p

	 and id = #{id} 
	 and accept_no = #{acceptNo} 
	 and custom_id = #{customId} 
	 and upload_ip = #{uploadIp} 
	 and upload_time = #{uploadTime} 
	 and image_path = #{imagePath} 
	 and image_name = #{imageName} 
	 and image_size = #{imageSize} 
	 and image_special = #{imageSpecial} 
	 and special_msg = #{specialMsg} 
	 and check_type_id = #{checkTypeId} 
	 and machine_id = #{machineId} 
	 and machine_check_time = #{machineCheckTime} 
	 and machine_result = #{machineResult} 
	 and machine_check_value = #{machineCheckValue} 
	 and machine_result_msg = #{machineResultMsg} 
	 and user_check_id = #{userCheckId} 

	  and date_format(p.user_check_begin, '%Y-%m-%d')  >=   date_format(#{userCheckBegin}, '%Y-%m-%d') 
	  and date_format(p.user_check_begin, '%Y-%m-%d')  <=  date_format(#{userCheckEnd}, '%Y-%m-%d') 
	  and date_format(p.user_check_begin, '%Y-%m-%d') BETWEEN  date_format(#{userCheckBegin}, '%Y-%m-%d') AND date_format(#{userCheckEnd}, '%Y-%m-%d') 

	 and user_result = #{userResult} 
	 and user_result_msg like CONCAT('%', #{userResultMsg}, '%') 
	 and final_result = #{finalResult} 
	 and deal_stage = #{dealStage} 
	 and create_time = #{createTime} 
	 and create_user = #{createUser} 
	 and update_time = #{updateTime} 
	 and update_user = #{updateUser} 
	 and remark = #{remark} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select AVG(timestampdiff(second, user_check_begin, user_check_end))
from pc_image_check

	 and id = #{id} 
	 and accept_no = #{acceptNo} 
	 and custom_id = #{customId} 
	 and upload_ip = #{uploadIp} 
	 and upload_time = #{uploadTime} 
	 and image_path = #{imagePath} 
	 and image_name = #{imageName} 
	 and image_size = #{imageSize} 
	 and image_special = #{imageSpecial} 
	 and special_msg = #{specialMsg} 
	 and check_type_id = #{checkTypeId} 
	 and machine_id = #{machineId} 
	 and machine_check_time = #{machineCheckTime} 
	 and machine_result = #{machineResult} 
	 and machine_check_value = #{machineCheckValue} 
	 and machine_result_msg = #{machineResultMsg} 
	 and user_check_id = #{userCheckId} 

	  and date_format(p.user_check_begin, '%Y-%m-%d')  >=   date_format(#{userCheckBegin}, '%Y-%m-%d') 
	  and date_format(p.user_check_begin, '%Y-%m-%d')  <=  date_format(#{userCheckEnd}, '%Y-%m-%d') 
	  and date_format(p.user_check_begin, '%Y-%m-%d') BETWEEN  date_format(#{userCheckBegin}, '%Y-%m-%d') AND date_format(#{userCheckEnd}, '%Y-%m-%d') 

	 and user_result = #{userResult} 
	 and user_result_msg like CONCAT('%', #{userResultMsg}, '%') 
	 and final_result = #{finalResult} 
	 and deal_stage = #{dealStage} 
	 and create_time = #{createTime} 
	 and create_user = #{createUser} 
	 and update_time = #{updateTime} 
	 and update_user = #{updateUser} 
	 and remark = #{remark} 


	limit #{offset}, #{limit}

